import{_ as e,o,c as r,X as a}from"./chunks/framework.28451963.js";const u=JSON.parse('{"title":"Docker简介","description":"","frontmatter":{},"headers":[],"relativePath":"serverless/system/docker简介.md","filePath":"serverless/system/docker简介.md","lastUpdated":1704191114000}'),c={name:"serverless/system/docker简介.md"},t=a('<h1 id="docker简介" tabindex="-1">Docker简介 <a class="header-anchor" href="#docker简介" aria-label="Permalink to &quot;Docker简介&quot;">​</a></h1><h2 id="_1、简介" tabindex="-1">1、简介 <a class="header-anchor" href="#_1、简介" aria-label="Permalink to &quot;1、简介&quot;">​</a></h2><p><code>Docker</code>是一种开源的容器化平台，它可以将应用程序及其依赖项打包到一个可移植的容器中，以便在任何地方运行。<code>Docker容器</code>是<code>Docker镜像</code>的<code>运行实例</code>，可在<code>任何支持Docker的平台</code>上运行。</p><p><code>Docker</code>的主要目标是<code>简化应用程序的部署和管理</code>。使用Docker,开发人员可以将应用程序及其所有依赖项打包到一个可移植的容器中，然后将该容器部署到任何支持Docker的平台上。这使得开发人员可以更快地构建和部署应用程序，并减少了管理多个环境所需的工作量。</p><h2 id="_2、安装" tabindex="-1">2、安装 <a class="header-anchor" href="#_2、安装" aria-label="Permalink to &quot;2、安装&quot;">​</a></h2><h3 id="_2-1、在linux安装docker" tabindex="-1">2.1、在linux安装docker <a class="header-anchor" href="#_2-1、在linux安装docker" aria-label="Permalink to &quot;2.1、在linux安装docker&quot;">​</a></h3><p><a href="/serverless/system/linux配置docker环境">linux安装docker</a></p><h3 id="_2-2、docker桌面端安装" tabindex="-1">2.2、docker桌面端安装 <a class="header-anchor" href="#_2-2、docker桌面端安装" aria-label="Permalink to &quot;2.2、docker桌面端安装&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>安装完docker桌面端，默认会安装docker环境</p></div><p>下载地址：<a href="https://www.docker.com/" target="_blank" rel="noreferrer">https://www.docker.com/</a></p>',10),d=[t];function s(l,i,n,k,_,h){return o(),r("div",null,d)}const m=e(c,[["render",s]]);export{u as __pageData,m as default};
