import{_ as t,o as e,c as a,X as l}from"./chunks/framework.28451963.js";const s="/serverless/linux.png",x=JSON.parse('{"title":"Linux简介","description":"","frontmatter":{},"headers":[],"relativePath":"serverless/system/linux简介.md","filePath":"serverless/system/linux简介.md","lastUpdated":1704191114000}'),n={name:"serverless/system/linux简介.md"},o=l('<h1 id="linux简介" tabindex="-1">Linux简介 <a class="header-anchor" href="#linux简介" aria-label="Permalink to &quot;Linux简介&quot;">​</a></h1><h2 id="_1、简介" tabindex="-1">1、简介 <a class="header-anchor" href="#_1、简介" aria-label="Permalink to &quot;1、简介&quot;">​</a></h2><p><code>Linux</code>是一套<code>免费使用和自由传播</code>的类Unix操作系统，是一个基于POSIX和UNIX的<code>多用户</code>、<code>多任务</code>、支持<code>多线程</code>和<code>多CPU</code>的操作系统。它能运行主要的UNIX工具软件、应用程序和网络协议。支持32位和64位硬件。</p><p>Linux继承了Unix以网络为核心的设计思想，是一个性能稳定的多用户网络操作系统。</p><h2 id="_2、系统目录结构" tabindex="-1">2、系统目录结构 <a class="header-anchor" href="#_2、系统目录结构" aria-label="Permalink to &quot;2、系统目录结构&quot;">​</a></h2><p><img src="'+s+`" alt="linux"></p><h3 id="_2-1、系统启动必须" tabindex="-1">2.1、系统启动必须 <a class="header-anchor" href="#_2-1、系统启动必须" aria-label="Permalink to &quot;2.1、系统启动必须&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/boot</td><td style="text-align:left;">存放的启动 Linux 时使用的内核文件，包括连接文件以及镜像文件。</td></tr><tr><td style="text-align:left;">/etc</td><td style="text-align:left;">存放所有的系统需要的配置文件和 子目录列表 ，更改目录下的文件可能会导致系统不能启动。</td></tr><tr><td style="text-align:left;">/lib</td><td style="text-align:left;">存放基本代码库（比如 c++库），其作用类似于 Windows 里的 DLL 文件。几乎所有的应用程序都需要用到这些共享库。</td></tr><tr><td style="text-align:left;">/sys</td><td style="text-align:left;">这是 Linux2.6 内核的一个很大的变化。该目录下安装了 2.6 内核中新出现的一个文件系统 sysfs 。</td></tr></tbody></table><h3 id="_2-2、指令集合" tabindex="-1">2.2、指令集合 <a class="header-anchor" href="#_2-2、指令集合" aria-label="Permalink to &quot;2.2、指令集合&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/bin</td><td style="text-align:left;">bin 是 Binaries（二进制文件） 的缩写，存放着最常用的程序和指令</td></tr><tr><td style="text-align:left;">/sbin</td><td style="text-align:left;">s 就是 Super User 的意思，是 Superuser Binaries（超级用户的二进制文件）的缩写，这里存放的是系统管理员使用的系统管理程序。</td></tr></tbody></table><h3 id="_2-3、外部文件管理" tabindex="-1">2.3、外部文件管理 <a class="header-anchor" href="#_2-3、外部文件管理" aria-label="Permalink to &quot;2.3、外部文件管理&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/dev</td><td style="text-align:left;">Device（设备）的缩写， 存放的是 Linux 的外部设备。<br><strong>注意：</strong> 在 Linux 中访问设备的方式和访问文件的方式是相同的。</td></tr><tr><td style="text-align:left;">/media</td><td style="text-align:left;">linux 系统会自动识别一些设备，例如 U 盘、光驱等等，当识别后，Linux 会把识别的设备挂载到这个目录下。</td></tr><tr><td style="text-align:left;">/mnt</td><td style="text-align:left;">临时挂载别的文件系统的，我们可以将光驱挂载在/mnt/上，然后进入该目录就可以查看光驱里的内容了。</td></tr></tbody></table><h3 id="_2-4、临时文件" tabindex="-1">2.4、临时文件 <a class="header-anchor" href="#_2-4、临时文件" aria-label="Permalink to &quot;2.4、临时文件&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/run</td><td style="text-align:left;">是一个临时文件系统，存储系统启动以来的信息。当系统重启时，这个目录下的文件应该被删掉或清除。如果你的系统上有 /var/run 目录，应该让它指向 run</td></tr><tr><td style="text-align:left;">/lost+found</td><td style="text-align:left;">这个目录一般情况下是空的，当系统非法关机后，这里就存放了一些文件。</td></tr><tr><td style="text-align:left;">/tmp</td><td style="text-align:left;">tmp 是 temporary(临时) 的缩写，这个目录是用来存放一些临时文件的。</td></tr></tbody></table><h3 id="_2-5、账户" tabindex="-1">2.5、账户 <a class="header-anchor" href="#_2-5、账户" aria-label="Permalink to &quot;2.5、账户&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/root</td><td style="text-align:left;">系统管理员的用户主目录。 该目录为系统管理员，也称作超级权限者的用户主目录。</td></tr><tr><td style="text-align:left;">/home</td><td style="text-align:left;">用户的主目录，在 Linux 中，每个用户都有一个自己的目录，一般该目录名是以用户的账号命名的，如上图中的 alice、bob 和 eve。</td></tr><tr><td style="text-align:left;">/usr</td><td style="text-align:left;">usr 是 unix shared resources(共享资源) 的缩写，这是一个非常重要的目录，用户的很多应用程序和文件都放在这个目录下，类似于 windows 下的 program files 目录。</td></tr><tr><td style="text-align:left;">/usr/bin</td><td style="text-align:left;">系统用户使用的应用程序与指令。</td></tr><tr><td style="text-align:left;">/usr/sbin</td><td style="text-align:left;">超级用户使用的比较高级的管理程序和系统守护程序。</td></tr><tr><td style="text-align:left;">/usr/src</td><td style="text-align:left;">内核源代码默认的放置目录。</td></tr></tbody></table><h3 id="_2-6、运行过程中要用" tabindex="-1">2.6、运行过程中要用 <a class="header-anchor" href="#_2-6、运行过程中要用" aria-label="Permalink to &quot;2.6、运行过程中要用&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/var</td><td style="text-align:left;">var 是 variable(变量) 的缩写，这个目录中存放着在不断扩充着的东西。<br>存放经常修改的数据，比如程序运行的日志文件（/var/log 目录下）。</td></tr><tr><td style="text-align:left;">/proc</td><td style="text-align:left;">proc 是 Processes(进程) 的缩写，管理内存空间虚拟的目录，是系统内存的映射，我们可以直接访问这个目录来，获取系统信息。这个目录的内容不在硬盘上而是在内存里，我们也可以直接修改里面的某些文件来做修改。</td></tr></tbody></table><h3 id="_2-7、扩展用的" tabindex="-1">2.7、扩展用的 <a class="header-anchor" href="#_2-7、扩展用的" aria-label="Permalink to &quot;2.7、扩展用的&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">目录</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:center;">/opt</td><td style="text-align:left;">opt 是 optional(可选) 的缩写，默认是空的。<br>这是给主机额外安装软件所摆放的目录。比如你安装一个 ORACLE 数据库则就可以放到这个目录下。</td></tr><tr><td style="text-align:center;">/srv</td><td style="text-align:left;">存放服务启动后需要提取的数据 <strong>(不用服务器就是空)</strong></td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">注意：</p><p>在 Linux 系统中，有几个目录是比较重要的，平时需要注意不要误删除或者随意更改内部文件。</p><ul><li><code>/etc</code>：这个是系统中的配置文件，如果你更改了该目录下的某个文件可能会导致系统不能启动。</li><li><code>/bin, /sbin, /usr/bin, /usr/sbin:</code> 这是系统预设的执行文件的放置目录，比如 <code>ls</code> 就是在 <code>/bin/ls</code> 目录下的。</li><li>值得提出的是<code>/bin、/usr/bin</code>是给系统用户使用的指令（除 root 外的通用用户），而<code>/sbin, /usr/sbin</code> 则是给 <code>root</code> 使用的指令。</li><li><code>/var</code>： 这是一个非常重要的目录，系统上跑了很多程序，那么每个程序都会有相应的日志产生，而这些日志就被记录到这个目录下，具体在 <code>/var/log</code> 目录下，另外 mail 的预设放置也是在这里</li></ul></div><h2 id="_3、yum" tabindex="-1">3、yum <a class="header-anchor" href="#_3、yum" aria-label="Permalink to &quot;3、yum&quot;">​</a></h2><h3 id="_3-1、简介" tabindex="-1">3.1、简介 <a class="header-anchor" href="#_3-1、简介" aria-label="Permalink to &quot;3.1、简介&quot;">​</a></h3><p><code>Yum</code>（Yet Another Setup Tool）是一款基于<code>RPM</code>包管理系统的<code>软件包管理工具</code>，主要用于在<code>Linux</code>操作系统上安装、升级和删除软件包。</p><h3 id="_3-2、yum使用" tabindex="-1">3.2、yum使用 <a class="header-anchor" href="#_3-2、yum使用" aria-label="Permalink to &quot;3.2、yum使用&quot;">​</a></h3><h4 id="_3-2-1、升级软件包" tabindex="-1">3.2.1、升级软件包 <a class="header-anchor" href="#_3-2-1、升级软件包" aria-label="Permalink to &quot;3.2.1、升级软件包&quot;">​</a></h4><p>升级软件包：使用<code>yum update</code>命令可以更新所有已安装软件包的最新版本。</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">update</span></span></code></pre></div><h4 id="_3-2-2、安装软件" tabindex="-1">3.2.2、安装软件 <a class="header-anchor" href="#_3-2-2、安装软件" aria-label="Permalink to &quot;3.2.2、安装软件&quot;">​</a></h4><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>安装软件：使用<code>yum install</code>命令可以安装软件包。</p></div><p>安装nginx</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">nginx</span></span></code></pre></div><h4 id="_3-2-3、搜索软件包" tabindex="-1">3.2.3、搜索软件包 <a class="header-anchor" href="#_3-2-3、搜索软件包" aria-label="Permalink to &quot;3.2.3、搜索软件包&quot;">​</a></h4><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>搜索软件包：可以使用yum search命令来搜索特定的软件包</p></div><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">search</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">python</span></span></code></pre></div><h4 id="_3-2-4、删除软件包" tabindex="-1">3.2.4、删除软件包 <a class="header-anchor" href="#_3-2-4、删除软件包" aria-label="Permalink to &quot;3.2.4、删除软件包&quot;">​</a></h4><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>删除软件包：使用<code>yum remove</code>命令可以删除软件包。</p></div><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">remove</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">python</span></span></code></pre></div><h4 id="_3-2-5、清理缓存" tabindex="-1">3.2.5、清理缓存 <a class="header-anchor" href="#_3-2-5、清理缓存" aria-label="Permalink to &quot;3.2.5、清理缓存&quot;">​</a></h4><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>在使用Yum进行软件包操作后，系统会缓存下载的软件包文件。您可以使用<code>yum clean</code>命令清理这些缓存文件</p></div><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">clean</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">all</span></span></code></pre></div><h2 id="_4、wget" tabindex="-1">4、wget <a class="header-anchor" href="#_4、wget" aria-label="Permalink to &quot;4、wget&quot;">​</a></h2><h3 id="_4-1、简介" tabindex="-1">4.1、简介 <a class="header-anchor" href="#_4-1、简介" aria-label="Permalink to &quot;4.1、简介&quot;">​</a></h3><p><code>wget</code>是一个自由的网络下载工具，可以用来下载网络上的资源。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>使用<code>wget</code>下载文件的基本语法是：wget [参数列表] URL。</p></div><h3 id="_4-2、下载整个http或者ftp站点、" tabindex="-1">4.2、下载整个http或者ftp站点、 <a class="header-anchor" href="#_4-2、下载整个http或者ftp站点、" aria-label="Permalink to &quot;4.2、下载整个http或者ftp站点、&quot;">​</a></h3><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-r</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">index.html</span></span></code></pre></div><h3 id="_4-3、下载文件并重命名" tabindex="-1">4.3、下载文件并重命名 <a class="header-anchor" href="#_4-3、下载文件并重命名" aria-label="Permalink to &quot;4.3、下载文件并重命名&quot;">​</a></h3><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">http://www.lxlinux.net</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--output-document</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">newfile.html</span></span>
<span class="line"><span style="color:#FFCB6B;">或者</span></span>
<span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">http://www.lxlinux.net</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-O</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">newfile.html</span></span></code></pre></div><h3 id="_4-4、断点续传" tabindex="-1">4.4、断点续传 <a class="header-anchor" href="#_4-4、断点续传" aria-label="Permalink to &quot;4.4、断点续传&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>适用于要下载的文件非常大，因为网络的原因有可能出现一次性无法下载完全的情况。</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--continue</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://www.lxlinux.net/linux-distro.iso</span></span>
<span class="line"><span style="color:#FFCB6B;">或者</span></span>
<span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-c</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://www.lxlinux.net/linux-distro.iso</span></span></code></pre></div><h3 id="_4-5、后台下载" tabindex="-1">4.5、后台下载 <a class="header-anchor" href="#_4-5、后台下载" aria-label="Permalink to &quot;4.5、后台下载&quot;">​</a></h3><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-b</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://www.lxlinux.net/linux-distro.iso</span></span></code></pre></div></div>`,51),i=[o];function r(d,c,p,h,y,u){return e(),a("div",null,i)}const g=t(n,[["render",r]]);export{x as __pageData,g as default};
